package com.example.navigationdrawertest.utils;

import org.jsoup.Jsoup;
import org.jsoup.nodes.Attribute;
import org.jsoup.nodes.Attributes;
import org.jsoup.nodes.Document;
import org.jsoup.nodes.Element;
import org.jsoup.parser.Tag;
import org.litepal.crud.DataSupport;

import android.os.Environment;
import android.text.Html;

import com.example.navigationdrawertest.application.OrientApplication;
import com.example.navigationdrawertest.model.Cell;
import com.example.navigationdrawertest.model.Operation;
import com.example.navigationdrawertest.model.Task;

public class HtmlHelper
{
	/**
	 * 2017-1-11 16:04:12 update by liuyangchao
	 * function:用于解决CELL_100表中Html字段和普通String字段混淆的问题
	 * @param str
	 * @return
	 */
	public static CharSequence transCellLabel(String str) {
        if (str.contains("<sup>") || str.contains("<sub>") || str.contains("<br>")){
            return Html.fromHtml(str);
        }else{
            return str;
        }
    }
	
	public static String checkedSrc = "data:image/png;base64,";
	public static String uncheckedSrc = "data:image/png;base64,";

	
	public static boolean writeTaskHtml(String taskPath, String htmlContent )
	{
 		return FileOperation.fileWriter(taskPath, htmlContent);
	}
	
	//改变文本框内容
	public static void changeTextValue(Document doc, Cell cell, String html){
		String htmlId = html;
		Element content = doc.getElementById(htmlId);	
		String valueText = cell.getOpvalue();
		content.attr("value", valueText);

		Task task = DataSupport.where("postinstanceid = ? and taskid = ? and rwid = ?", cell.getmTTID(), cell.getTaskid()
				,OrientApplication.getApplication().rw.getRwid()).find(Task.class).get(0);
		
		HtmlHelper.saveHtmlDoc(task, doc);
	}
	
	//改变打钩项内容
	public static void changeCheckValue(Document doc, Cell cell, String html){
//		String htmlId = cell.getCellid();
		String htmlId = html;
		Element content = doc.getElementById(htmlId);	
		String baseuri = content.baseUri();
		Tag imageTag = Tag.valueOf("img");
		Element imgEl = new Element(imageTag,baseuri);
		if(cell.getIshook().equals("is"))			//打钩状态
		{
			Attribute at = new Attribute("src",checkedSrc);	
			Attribute idAt= new Attribute("id",htmlId);
			imgEl.attributes().put(idAt);
			imgEl.attributes().put(at);
		}else{			//不打钩状态
			Attribute at = new Attribute("src",uncheckedSrc);	
			Attribute idAt= new Attribute("id",htmlId);
			imgEl.attributes().put(idAt);
			imgEl.attributes().put(at);
		}
		content.replaceWith(imgEl);
//		Task task = DataSupport.where("userid = ? and taskid = ? and rwid = ?", cell.getUserid(), cell.getTaskid()
//				,OrientApplication.getApplication().rw.getRwid()).find(Task.class).get(0);
		Task task = DataSupport.where("postinstanceid = ? and taskid = ? and rwid = ?", cell.getmTTID(), cell.getTaskid()
				,OrientApplication.getApplication().rw.getRwid()).find(Task.class).get(0);
		HtmlHelper.saveHtmlDoc(task, doc);
	}
	
	//改变照片项内容
	public static void changePhotoValue(Document doc, Operation operation){

		if(operation != null)
		{
			Element content = doc.getElementById(operation.getCellid());
			String baseuri = content.baseUri();
			Tag inputTag = Tag.valueOf("input");
			Element photoEl = new Element(inputTag, baseuri);
			
			//为Element添加属性
			String idValue = "P"+operation.getOperationid();
			String clickValue = "self.showImage('"+operation.getOperationid()+"')";
			Attribute id = new Attribute("id",idValue);	
			Attribute type = new Attribute("type","button");	
			Attribute value = new Attribute("value","照片");	
			Attribute onClick = new Attribute("onClick",clickValue);	
			Attribute style = new Attribute("style","background-color:#6C9EDD");	
			
			Attributes attributes = photoEl.attributes();
			attributes.put(id);
			attributes.put(type);
			attributes.put(value);
			attributes.put(onClick);
			attributes.put(style);
			
			if(doc.getElementById(idValue) == null || "".equals(doc.getElementById(idValue)))
			{
				content.after(photoEl);
				photoEl.before("&nbsp;&nbsp;");
			}
			Task task = DataSupport.where("taskid = ? and rwid = ?", operation.getTaskid()
					,OrientApplication.getApplication().rw.getRwid()).find(Task.class).get(0);
			HtmlHelper.saveHtmlDoc(task, doc);
		}
	}
	
	//获取htmlDocument对象
	public static Document getHtmlDoc(Task task)
	{

		String filePath = Environment.getDataDirectory().getPath() + Config.packagePath
				+ Config.htmlPath+ "/"+ task.getPostname()+"/" + task.getTaskid();
		
		
		String htmlContent = FileOperation.fileReader(filePath);		
		Document doc = null;
		doc = Jsoup.parse(htmlContent);
		return doc;
	}

	//保存html的Document到本地
	public static boolean saveHtmlDoc(Task task,Document document)
	{	
//		String filePath = Environment.getDataDirectory().getPath() + Config.packagePath
//			+ Config.htmlPath+ "/"+ task.getUserid()+"/" + task.getTaskid();
		String filePath = Environment.getDataDirectory().getPath() + Config.packagePath
				+ Config.htmlPath+ "/"+ task.getPostname()+"/" + task.getTaskid();
		String changedHtml  = document.toString();
		if(!FileOperation.fileWriter(filePath, changedHtml))
		{
			return false;
		}
		return true;
	}
}
